tool.command:

ln -f -s ${input_structure_path} ${input_structure_path}.${input_structure_path.ext};

ln -f -s ${input_traj_path} ${input_traj_path}.${input_traj_path.ext};

ln -f -s ${input_index_path} ${input_index_path}.${input_index_path.ext};

#if $config.sele == "option1":
ln -s -f ${config.properties} ${config.properties}.${config.properties.ext};
#end if
docker run --rm -v $__root_dir__/database/files:$__root_dir__/database/files quay.io/biocontainers/biobb_analysis:3.0.0--py_0 gmx_cluster
#if $config.sele == "option1":
--config ${config.properties}.${config.properties.ext}
#else if $config.sele == "option2":
--config ${config.jsonstr}
#else if $config.sele == "option4":
--config "__oc____dq__fit_selection__dq__:__dq__${config.fit_selection}__dq__,__dq__output_selection__dq__:__dq__${config.output_selection}__dq__,__dq__dista__dq__:__dq__${config.dista}__dq__,__dq__method__dq__:__dq__${config.method}__dq__,__dq__cutoff__dq__:__dq__${config.cutoff}__dq__,__dq__gmx_path__dq__:__dq__${config.gmx_path}__dq____cc__"
#end if

--input_structure_path ${input_structure_path}.${input_structure_path.ext}

--input_traj_path ${input_traj_path}.${input_traj_path.ext}

--input_index_path ${input_index_path}.${input_index_path.ext}


--output_pdb_path $__root_dir__/database/files/000/$outname_output_pdb_path
;          
#if $config.sele == "option1":
rm -f ${config.properties}.${config.properties.ext};
#end if

rm -f ${input_structure_path}.${input_structure_path.ext};

rm -f ${input_traj_path}.${input_traj_path.ext};

rm -f ${input_index_path}.${input_index_path.ext};


if test -f $__root_dir__/database/files/000/$outname_output_pdb_path; then mv $__root_dir__/database/files/000/$outname_output_pdb_path $output_pdb_path; fi;
