tool.command:

ln -f -s ${input_energy_path} ${input_energy_path}.${input_energy_path.ext};

#if $config.sele == "option1":
ln -s -f ${config.properties} ${config.properties}.${config.properties.ext};
#end if
docker run -v $__root_dir__/database/files:$__root_dir__/database/files quay.io/biocontainers/biobb_analysis:3.0.0--py_0 gmx_energy
#if $config.sele == "option1":
--config ${config.properties}.${config.properties.ext}
#else if $config.sele == "option2":
--config ${config.jsonstr}
#end if

--input_energy_path ${input_energy_path}.${input_energy_path.ext}


--output_xvg_path $__root_dir__/database/files/000/$outname_output_xvg_path
;         
#if $config.sele == "option1":
rm -f ${config.properties}.${config.properties.ext};
#end if

rm -f ${input_energy_path}.${input_energy_path.ext};


if test -f $__root_dir__/database/files/000/$outname_output_xvg_path; then mv $__root_dir__/database/files/000/$outname_output_xvg_path $output_xvg_path; fi;
