tool.command:

ln -f -s ${input_path} ${input_path}.${input_path.ext};

#if $config.sele == "option1":
ln -s -f ${config.properties} ${config.properties}.${config.properties.ext};
#end if
docker run -v $__root_dir__/database/files:$__root_dir__/database/files quay.io/biocontainers/biobb_chemistry:3.0.0--py_0 acpype_params_ac
#if $config.sele == "option1":
--config ${config.properties}.${config.properties.ext}
#else if $config.sele == "option2":
--config ${config.jsonstr}
#end if

--input_path ${input_path}.${input_path.ext}


--output_path_frcmod $__root_dir__/database/files/000/$outname_output_path_frcmod

--output_path_inpcrd $__root_dir__/database/files/000/$outname_output_path_inpcrd

--output_path_lib $__root_dir__/database/files/000/$outname_output_path_lib

--output_path_prmtop $__root_dir__/database/files/000/$outname_output_path_prmtop
;         
#if $config.sele == "option1":
rm -f ${config.properties}.${config.properties.ext};
#end if

rm -f ${input_path}.${input_path.ext};


mv  $__root_dir__/database/files/000/${outname_output_path_frcmod} ${output_path_frcmod};

mv  $__root_dir__/database/files/000/${outname_output_path_inpcrd} ${output_path_inpcrd};

mv  $__root_dir__/database/files/000/${outname_output_path_lib} ${output_path_lib};

mv  $__root_dir__/database/files/000/${outname_output_path_prmtop} ${output_path_prmtop};
