tool.command:

ln -s $input_solute_gro_path ./input_solute_gro_path.$input_solute_gro_path.ext;
ln -s $input_top_zip_path ./input_top_zip_path.$input_top_zip_path.ext;
#if $input_solvent_gro_path:
  ln -s $input_solvent_gro_path ./input_solvent_gro_path.$input_solvent_gro_path.ext;
#end if
#if $config_json:
  ln -s $config_json ./config_json.$config_json.ext;
#end if

solvate

#if $config_json:
  --config ./config_json.$config_json.ext
#end if

--input_solute_gro_path ./input_solute_gro_path.$input_solute_gro_path.ext
--input_top_zip_path ./input_top_zip_path.$input_top_zip_path.ext
#if $input_solvent_gro_path:
  --input_solvent_gro_path ./input_solvent_gro_path.$input_solvent_gro_path.ext
#end if
--output_gro_path $outname_output_gro_path
--output_top_zip_path $outname_output_top_zip_path
;

mv $outname_output_gro_path $output_gro_path;
mv $outname_output_top_zip_path $output_top_zip_path;
