tool.command:

ln -s $input_pdb_path ./input_pdb_path.$input_pdb_path.ext;
#if $config_json:
  ln -s $config_json ./config_json.$config_json.ext;
#end if

haddock3_accessibility

#if $config_json:
  --config ./config_json.$config_json.ext
#end if

--input_pdb_path ./input_pdb_path.$input_pdb_path.ext
--output_accessibility_path $outname_output_accessibility_path
#if $outname_output_actpass_path:
  --output_actpass_path $outname_output_actpass_path
#end if
;

mv $outname_output_accessibility_path $output_accessibility_path;
if test -f $outname_output_actpass_path; then mv $outname_output_actpass_path $output_actpass_path; fi;
