#if $config_json:
  --config ./config_json.$config_json.ext
#end if

--haddock_config_path ./haddock_config_path.$haddock_config_path.ext
--input_haddock_wf_data_zip $outname_input_haddock_wf_data_zip
--output_haddock_wf_data_zip $outname_output_haddock_wf_data_zip
;

mv $outname_input_haddock_wf_data_zip $input_haddock_wf_data_zip;
