tool.command:

ln -f -s ${input_tpr_path} ${input_tpr_path}.${input_tpr_path.ext};

#if $config.sele == "option1":
ln -s -f ${config.properties} ${config.properties}.${config.properties.ext};
#end if
docker run -v $__root_dir__/database/files:$__root_dir__/database/files quay.io/biocontainers/biobb_md:3.0.0--py_0 mdrun
#if $config.sele == "option1":
--config ${config.properties}.${config.properties.ext}
#else if $config.sele == "option2":
--config ${config.jsonstr}
#end if

--input_tpr_path ${input_tpr_path}.${input_tpr_path.ext}


--output_trr_path $__root_dir__/database/files/000/$outname_output_trr_path

--output_gro_path $__root_dir__/database/files/000/$outname_output_gro_path

--output_edr_path $__root_dir__/database/files/000/$outname_output_edr_path

--output_log_path $__root_dir__/database/files/000/$outname_output_log_path

--output_xtc_path $__root_dir__/database/files/000/$outname_output_xtc_path

--output_cpt_path $__root_dir__/database/files/000/$outname_output_cpt_path

--output_dhdl_path $__root_dir__/database/files/000/$outname_output_dhdl_path
;         
#if $config.sele == "option1":
rm -f ${config.properties}.${config.properties.ext};
#end if

rm -f ${input_tpr_path}.${input_tpr_path.ext};


mv  $__root_dir__/database/files/000/${outname_output_trr_path} ${output_trr_path};

mv  $__root_dir__/database/files/000/${outname_output_gro_path} ${output_gro_path};

mv  $__root_dir__/database/files/000/${outname_output_edr_path} ${output_edr_path};

mv  $__root_dir__/database/files/000/${outname_output_log_path} ${output_log_path};

mv  $__root_dir__/database/files/000/${outname_output_xtc_path} ${output_xtc_path};

mv  $__root_dir__/database/files/000/${outname_output_cpt_path} ${output_cpt_path};

mv  $__root_dir__/database/files/000/${outname_output_dhdl_path} ${output_dhdl_path};
