tool.command:

ln -f -s ${input_pdb_path} ${input_pdb_path}.${input_pdb_path.ext};

#if $config.sele == "option1":
ln -s -f ${config.properties} ${config.properties}.${config.properties.ext};
#end if
docker run -v $__root_dir__/database/files:$__root_dir__/database/files quay.io/biocontainers/biobb_md:3.0.0--py_0 pdb2gmx
#if $config.sele == "option1":
--config ${config.properties}.${config.properties.ext}
#else if $config.sele == "option2":
--config ${config.jsonstr}
#end if

--input_pdb_path ${input_pdb_path}.${input_pdb_path.ext}


--output_gro_path $__root_dir__/database/files/000/$outname_output_gro_path

--output_top_zip_path $__root_dir__/database/files/000/$outname_output_top_zip_path
;         
#if $config.sele == "option1":
rm -f ${config.properties}.${config.properties.ext};
#end if

rm -f ${input_pdb_path}.${input_pdb_path.ext};


mv  $__root_dir__/database/files/000/${outname_output_gro_path} ${output_gro_path};

mv  $__root_dir__/database/files/000/${outname_output_top_zip_path} ${output_top_zip_path};
