tool.command:

ln -f -s ${input_dataset_path} ${input_dataset_path}.${input_dataset_path.ext};

#if $config.sele == "option1":
ln -s -f ${config.properties} ${config.properties}.${config.properties.ext};
#end if
docker run --rm -v $__root_dir__/database/files:$__root_dir__/database/files quay.io/biocontainers/biobb_ml:3.0.1--py_0 map_variables
#if $config.sele == "option1":
--config ${config.properties}.${config.properties.ext}
#else if $config.sele == "option2":
--config ${config.jsonstr}
#end if

--input_dataset_path ${input_dataset_path}.${input_dataset_path.ext}


--output_dataset_path $__root_dir__/database/files/000/$outname_output_dataset_path
;          
#if $config.sele == "option1":
rm -f ${config.properties}.${config.properties.ext};
#end if

rm -f ${input_dataset_path}.${input_dataset_path.ext};


if test -f $__root_dir__/database/files/000/$outname_output_dataset_path; then mv $__root_dir__/database/files/000/$outname_output_dataset_path $output_dataset_path; fi;
