tool.command:

  ln -f -s ${input_solute_gro_path} ${input_solute_gro_path}.${input_solute_gro_path.ext};

  ln -f -s ${input_top_zip_path} ${input_top_zip_path}.${input_top_zip_path.ext};

#if $config.sele == "option1":
  ln -s -f ${config.properties} ${config.properties}.${config.properties.ext};
#end if
docker run --rm -v $__root_dir__/database/files:$__root_dir__/database/files quay.io/biocontainers/biobb_md:3.0.0--py_0 solvate
#if $config.sele == "option1":
  --config ${config.properties}.${config.properties.ext}
#else if $config.sele == "option2":
  --config ${config.jsonstr}
#else if $config.sele == "option4":
  --config "__oc____dq__input_solvent_gro_path__dq__:__dq__${config.input_solvent_gro_path}__dq__,__dq__gmx_path__dq__:__dq__${config.gmx_path}__dq____cc__"
#end if

  --input_solute_gro_path ${input_solute_gro_path}.${input_solute_gro_path.ext}

  --input_top_zip_path ${input_top_zip_path}.${input_top_zip_path.ext}


  --output_gro_path $__root_dir__/database/files/000/$outname_output_gro_path

  --output_top_zip_path $__root_dir__/database/files/000/$outname_output_top_zip_path
;          
#if $config.sele == "option1":
  rm -f ${config.properties}.${config.properties.ext};
#end if

  rm -f ${input_solute_gro_path}.${input_solute_gro_path.ext};

  rm -f ${input_top_zip_path}.${input_top_zip_path.ext};


  if test -f $__root_dir__/database/files/000/$outname_output_gro_path; then mv $__root_dir__/database/files/000/$outname_output_gro_path $output_gro_path; fi;

  if test -f $__root_dir__/database/files/000/$outname_output_top_zip_path; then mv $__root_dir__/database/files/000/$outname_output_top_zip_path $output_top_zip_path; fi;
